#pragma once#include <string>#include <vector>#include <nlohmann/json.hpp>using json = nlohmann::json;struct ShopItem {    std::string itemId;    int price;    int stock;    NLOHMANN_DEFINE_TYPE_INTRUSIVE(ShopItem, itemId, price, stock)};struct NpcData {    std::string npcId;    std::string npcName;    std::string portraitPath;    std::vector<std::string> dialogueLines;    // Comportamentos opcionais    bool isMerchant = false;    std::vector<ShopItem> shopInventory;    bool hasQuests = false;    std::vector<std::string> questIds;    // Propriedades    float interactionRange = 2.0f;    bool canMove = false;    NLOHMANN_DEFINE_TYPE_INTRUSIVE(NpcData, npcId, npcName, portraitPath,        dialogueLines, isMerchant, shopInventory, hasQuests, questIds,        interactionRange, canMove)};