// NpcFactory.cpp#include "npcs/npcFactory.h"#include <fstream>#include <iostream>void NpcFactory::loadAllNpcs(const std::string& directory) {    for (const auto& entry : std::filesystem::directory_iterator(directory)) {        if (entry.path().extension() == ".json") {            try {                std::ifstream file(entry.path());                json j;                file >> j;                NpcData data = j.get<NpcData>();                npcDatabase[data.npcId] = data;                std::cout << "Loaded NPC: " << data.npcName << std::endl;            }            catch (const std::exception& e) {                std::cerr << "Error loading " << entry.path()                    << ": " << e.what() << std::endl;            }        }    }}std::unique_ptr<FriendlyNpc> NpcFactory::createNpc(const std::string& npcId) {    auto it = npcDatabase.find(npcId);    if (it == npcDatabase.end()) {        throw std::runtime_error("NPC not found: " + npcId);    }    return std::make_unique<FriendlyNpc>(it->second);}bool NpcFactory::hasNpc(const std::string& npcId) const {    return npcDatabase.find(npcId) != npcDatabase.end();}